declare module 'google-trends-api' {
    export interface DailyTrendsOptions {
      geo: string;
      trendDate?: Date;
      hl?: string;
    }
  
    export interface DailyTrendsResults {
      // Define the structure based on the expected results
      default: {
        trendingSearchesDays: Array<{
          date: string;
          formattedDate: string;
          trendingSearches: Array<{
            title: {
              query: string;
              exploreLink: string;
            };
            formattedTraffic: string;
            relatedQueries: Array<{
              query: string;
              exploreLink: string;
            }>;
            articles: Array<{
              title: string;
              timeAgo: string;
              source: string;
              image: {
                newsUrl: string;
                source: string;
                imageUrl: string;
              };
            }>;
          }>;
        }>;
      };
    }
  
    export function dailyTrends(options: DailyTrendsOptions): Promise<string>;
  }
  